import { useQuery, gql } from "@apollo/client";
import React, { useState } from "react";
import styled from "styled-components";

import ClinicalTrials from "./ClinicalTrials";

const Layout = styled.div`
  background: #f6f7fa;
  display: flex;
  height: 100%;
  justify-content: center;
  width: 100%;
`;

const Content = styled.div`
  margin-top: 48px;
  max-width: 1300px;
  width: 100%;
`;

const clinicalTrialsQuery = gql`
  query ClinicalTrials {
    locations {
      id
      name
      description
      photo
    }
  }
`;

export type PatientsSortDirection = "asc" | "desc" | null;

const App: React.FC = () => {
  const [patientsSortDirection, setPatientsSortDirection] =
    useState<PatientsSortDirection>(null);
  return (
    <Layout>
      <Content>
        {" "}
        <ClinicalTrials
          patientsSortDirection={patientsSortDirection}
          setPatientsSortDirection={setPatientsSortDirection}
          clinicalTrials={[{ site: "site", country: "country", patients: 2 }]}
        />
      </Content>
    </Layout>
  );
};

export default App;
